async function getChatBotResponse(messagesHistory, setMessagesHistory) {
    const response = await fetch("http:/localhost:5000/api/chatbot", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ message: messagesHistory}),
    });
    const data = await response.json();
    setMessagesHistory([...messagesHistory, { role: "system", content: data.message }]);
  }

export default getChatBotResponse;
